import React, { useState } from "react";
import { Link } from "react-router-dom";


function LoginForm() {
  const [inputs, setInputs] = useState({});
//   const adminUser = {
//     email : "admin01@gmail.com",
//     password : "0000"
//   }
//   const [user, setUser] = useState ({name:"",email:""});
