import Axios from "axios";
import { useState } from "react";

function RegisterForm() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const [registerList, setRegisterList] = useState([]);

  const getRegister = () => {
    Axios.get("http://localhost:3001/registers").then((response) => {
      setRegisterList(response.data);
    });
  };
  return (
    <div className="Form">
      <h1>CP University </h1>
      <h2>Sign Up</h2>
      <div className="information">
        <form action="">
          <div className="mb-3">
            <label htmlFor="firstName" className="form-label">
              Fisrt Name :
            </label>
            <input
              type="text"
              classname="form-control"
              placeholder="Enter First Name"
              onChange={(event) => {
                setFirstName(event.target.value)
              }}
            ></input>
          </div>

          <div className="mb-3">
            <label htmlFor="lastName" className="form-label">
              Last Name :
            </label>
            <input
              type="text"
              classname="form-control"
              placeholder="Enter Last name"
              onChange={(event) => {
                setLastName(event.target.value)
              }}
            ></input>
          </div>

          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email :
            </label>
            <input
              type="text"
              classname="form-control"
              placeholder="Enter Email"
              onChange={(event) => {
                setEmail(event.target.value)
              }}
            ></input>
          </div>

          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Password :
            </label>
            <input
              type="password"
              classname="form-control"
              placeholder="Enter Password"
              onChange={(event) => {
                setPassword(event.target.value)
              }}
            ></input>
          </div>

          <div className="mb-3">
            <label htmlFor="confirmPassword" className="form-label">
              Confirm Password :
            </label>
            <input
              type="password"
              classname="form-control"
              placeholder="Enter Confirm Password"
              onChange={(event) => {
                setConfirmPassword(event.target.value)
              }}
            ></input>
          </div>
          <button className="btn-register">Register</button>
        </form>
      </div>
      <hr />
      <div className="register">
        <button classname="btn-showdata" onClick={getRegister}>
          Show data
        </button>
        <br/> <br/>

        {registerList.map((val, key) => {
          return (
            <div className="register card">
              <div className="card-body text-left">
                <p className="card-text">First Name: {val.firstname}</p>
                <p className="card-text">Last Name: {val.lastname}</p>
                <p className="card-text">Email: {val.email}</p>
                <p className="card-text">Password: {val.password}</p>
                <p className="card-text">
                  Confirm Password: {val.confirmpassword}
                </p>
                <div className="d-flex"></div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default RegisterForm;
